// Controllers/UrlController.cs
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;

namespace WebAPI.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlController : ControllerBase
    {
        private readonly ILogger<UrlController> _logger;

        public UrlController(ILogger<UrlController> logger)
        {
            _logger = logger;
        }

        [HttpPost]
        public async Task<IActionResult> ExtractUrlData(string url)
        {
            try
            {
                var urlData = new UrlData();

                // Extract the protocol
                var protocol = url.Substring(0, url.IndexOf("://"));
                urlData.Protocol = protocol;

                // Extract the hostname
                var hostname = url.Substring(protocol.Length + 3);
                hostname = hostname.Substring(0, hostname.IndexOf("/"));
                urlData.Hostname = hostname;

                // Extract the port
                var port = url.Substring(hostname.Length + 3);
                port = port.Substring(0, port.IndexOf("/"));
                urlData.Port = int.Parse(port);

                // Extract the path
                var path = url.Substring(hostname.Length + port.Length + 3);
                urlData.Path = path;

                return Ok(urlData);
            }
            catch (Exception e)
            {
                _logger.LogError(e, "Error extracting URL data");
                return BadRequest();
            }
        }
    }

    // Models/UrlData.cs
