using System;
using System.Net.Http;
using System.Text.RegularExpressions;

public class UrlDataExtractor
{
    public static UrlData Extract(string url)
    {
        if (string.IsNullOrEmpty(url))
        {
            throw new ArgumentException("Invalid URL", nameof(url));
        }

        var httpClient = new HttpClient();
        var response = httpClient.GetAsync(url).Result;

        if (!response.IsSuccessStatusCode)
        {
            throw new HttpRequestException($"Failed to retrieve URL: {url}");
        }

        var content = response.Content.ReadAsStringAsync().Result;

        var title = Regex.Match(content, @"<title>(?<title>.*)</title>").Groups["title"].Value;
        var description = Regex.Match(content, @"<meta\s+name=""description""\s+content=""(?<description>.*)""").Groups["description"].Value;
        var image = Regex.Match(content, @"<meta\s+name=""image""\s+content=""(?<image>.*)""").Groups["image"].Value;

        return new UrlData
        {
            Title = title,
            Description = description,
            Image = image
        };
    }
}

public class UrlData
{
    public string Title { get; set; }
    public string Description { get; set; }
    public string Image { get; set; }
}
