using System.Net.Http;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;

namespace MyApi.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class DownloadController : ControllerBase
    {
        [HttpGet]
        [Route("download")]
        public async Task<IActionResult> Download(string url)
        {
            // TODO: Validate the input URL

            // Create an HTTP client to download the data
            var httpClient = new HttpClient();

            // Download the data from the URL
            var response = await httpClient.GetAsync(url);

            // Check if the download was successful
            if (!response.IsSuccessStatusCode)
            {
                return BadRequest($"Failed to download data from {url}");
            }

            // Read the response data as a stream
            var stream = await response.Content.ReadAsStreamAsync();

            // Create a new file to save the data
            var filePath = Path.Combine(Directory.GetCurrentDirectory(), "Data", "downloaded.txt");
            using (var fileStream = new FileStream(filePath, FileMode.Create))
            {
                // Copy the stream to the file
                await stream.CopyToAsync(fileStream);
            }

            // Return a success message
            return Ok($"Data downloaded from {url} and saved to {filePath}");
        }
    }
}
