using System;
using System.Collections.Generic;
using System.Linq;
using System.Net.Http;
using System.Text;
using System.Text.RegularExpressions;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;

namespace UrlDataExtractor.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlDataController : ControllerBase
    {
        [HttpPost]
        public async Task<ActionResult<UrlData>> GetUrlData(string url)
        {
            var httpClient = new HttpClient();
            var response = await httpClient.GetAsync(url);
            var content = await response.Content.ReadAsStringAsync();

            var data = new UrlData
            {
                Title = GetTitle(content),
                Description = GetDescription(content),
                Keywords = GetKeywords(content)
            };

            return Ok(data);
        }

        private string GetTitle(string content)
        {
            var titleMatch = Regex.Match(content, "<title>(.*?)</title>");
            return titleMatch.Groups[1].Value;
        }

        private string GetDescription(string content)
        {
            var descriptionMatch = Regex.Match(content, "<meta name=\"description\" content=\"(.*?)\">");
            return descriptionMatch.Groups[1].Value;
        }

        private string GetKeywords(string content)
        {
            var keywordsMatch = Regex.Match(content, "<meta name=\"keywords\" content=\"(.*?)\">");
            return keywordsMatch.Groups[1].Value;
        }
    }
}

