using System.Net;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Newtonsoft.Json;

namespace UrlData.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class UrlDataController : ControllerBase
    {
        [HttpGet("{url}")]
        public async Task<IActionResult> GetUrlData(string url)
        {
            // Validate input
            if (string.IsNullOrWhiteSpace(url))
            {
                return BadRequest("Invalid URL");
            }

            // Create HttpClient
            using (var client = new HttpClient())
            {
                // Set request headers
                client.DefaultRequestHeaders.Accept.Add(new MediaTypeWithQualityHeaderValue("application/json"));

                // Make request
                var response = await client.GetAsync(url);

                // Check response status code
                if (response.StatusCode != HttpStatusCode.OK)
                {
                    return StatusCode((int)response.StatusCode, "Error retrieving data");
                }

                // Read response data
                var responseString = await response.Content.ReadAsStringAsync();

                // Deserialize response data
                var data = JsonConvert.DeserializeObject<UrlData>(responseString);

                // Return data
                return Ok(data);
            }
        }
    }
}

