            using (var fileStream = new FileStream(_filePath, FileMode.Open, FileAccess.Read))
            {
                using (var reader = new StreamReader(fileStream))
                {
                    string line;
                    while ((line = await reader.ReadLineAsync()) != null)
                    {
                        data.Add(line);
                    }
                }
            }
            return data;
        }
    }
