using System;
using System.Net.Http;
using System.Text.RegularExpressions;

public class UrlDataExtractor
{
    public static string ExtractData(string url)
    {
        if (string.IsNullOrEmpty(url))
        {
            throw new ArgumentNullException(nameof(url));
        }

        var httpClient = new HttpClient();
        var response = httpClient.GetAsync(url).Result;

        if (!response.IsSuccessStatusCode)
        {
            throw new HttpRequestException($"Failed to retrieve data from {url}");
        }

        var content = response.Content.ReadAsStringAsync().Result;
        var match = Regex.Match(content, @"\bdata\b");

        if (!match.Success)
        {
            throw new InvalidOperationException($"Could not find data in {url}");
        }

        var data = match.Groups[1].Value;
        return data;
    }
}
