            var domainName = new Uri(url).Host;

            // Extract the path (e.g. /path/to/file)
            var path = new Uri(url).AbsolutePath;

            // Extract the query string (e.g. ?param1=value1&param2=value2)
            var queryString = new Uri(url).Query;

            // Extract the anchor (e.g. #anchor)
            var anchor = new Uri(url).Fragment;

            // Add the extracted data to the dictionary
            urlData.Add("protocol", protocol);
            urlData.Add("domain_name", domainName);
            urlData.Add("path", path);
            urlData.Add("query_string", queryString);
