// Controllers/UrlController.cs

using Microsoft.AspNetCore.Mvc;

namespace UrlExtractor.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class UrlController : ControllerBase
    {
        [HttpPost("extract")]
        public ActionResult<UrlData> Extract(string url)
        {
            if (string.IsNullOrEmpty(url))
            {
                return BadRequest("Invalid URL");
            }

            var urlData = new UrlData();

            // Extract the protocol
            var protocol = GetProtocol(url);
            urlData.Protocol = protocol;

            // Extract the domain
            var domain = GetDomain(url);
            urlData.Domain = domain;

            // Extract the path
            var path = GetPath(url);
            urlData.Path = path;

            // Extract the query string
            var queryString = GetQueryString(url);
            urlData.QueryString = queryString;

            return Ok(urlData);
        }

        private string GetProtocol(string url)
        {
            return url.Substring(0, url.IndexOf("://"));
        }

        private string GetDomain(string url)
        {
            var domain = url.Substring(url.IndexOf("://") + 3);
            domain = domain.Substring(0, domain.IndexOf("/"));
            return domain;
        }

        private string GetPath(string url)
        {
            var path = url.Substring(url.IndexOf(GetDomain(url)) + GetDomain(url).Length);
            return path;
        }

        private string GetQueryString(string url)
        {
            var queryString = url.Substring(url.IndexOf("?") + 1);
            return queryString;
        }
    }
}

