    {
        // Get the response content as a string
        var content = await response.Content.ReadAsStringAsync();

        // Extract the content from the string
        var extractedContent = ExtractContent(content);

        // Return the extracted content
        return Ok(extractedContent);
    }
    else
    {
        // Return an error message if the request was not successful
        return BadRequest("Unable to extract content from URL");
    }
}

private string ExtractContent(string content)
{
    // Implement your content extraction logic here
    // For example, you could use a regular expression to extract the content
    // between the <body> and </body> tags
    var bodyStartIndex = content.IndexOf("<body");
    var bodyEndIndex = content.IndexOf("</body>");
    var extractedContent = content.Substring(bodyStartIndex, bodyEndIndex - bodyStartIndex);
    return extractedContent;
