using System.Net.Http;
using System.Text.RegularExpressions;

namespace MyApp.Utilities
{
    public static class UrlDataExtractor
    {
        public static string ExtractData(string url)
        {
            // Check if the URL is valid
            if (!Uri.TryCreate(url, UriKind.Absolute, out Uri uri))
            {
                throw new ArgumentException("Invalid URL", nameof(url));
            }

            // Create a new HttpClient instance
            using (var client = new HttpClient())
            {
                // Send a GET request to the URL
                var response = client.GetAsync(uri).Result;

                // Check if the response is successful
                if (response.IsSuccessStatusCode)
                {
                    // Read the response content as a string
                    var content = response.Content.ReadAsStringAsync().Result;

                    // Extract the data from the response content
                    var data = ExtractDataFromContent(content);

                    return data;
                }
                else
                {
                    throw new HttpRequestException($"HTTP error {response.StatusCode}");
                }
            }
        }

        private static string ExtractDataFromContent(string content)
        {
            // Use regular expressions to extract the data from the response content
            var regex = new Regex(@"\bdata\b");
            var match = regex.Match(content);

            if (match.Success)
            {
                return match.Groups[1].Value;
            }
            else
            {
                throw new Exception("Data not found in response content");
            }
        }
    }
}
