// Controllers/UrlDataController.cs
using Microsoft.AspNetCore.Mvc;
using System.Text.RegularExpressions;

namespace UrlDataExtractor.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlDataController : ControllerBase
    {
        [HttpGet("{url}")]
        public ActionResult<UrlData> GetUrlData(string url)
        {
            // Validate input
            if (string.IsNullOrEmpty(url))
            {
                return BadRequest("Invalid URL");
            }

            // Extract data from URL
            var data = ExtractDataFromUrl(url);

            // Return data
            return Ok(data);
        }

        private UrlData ExtractDataFromUrl(string url)
        {
            // Initialize data
            var data = new UrlData();

            // Match URL against regular expression
            var match = Regex.Match(url, @"\b(?:https?://|www\.)(?:[^\s()<>]+|\(([^\s()<>]+|(\([^\s()<>]+\)))*\))+(?:\(([^\s()<>]+|(\([^\s()<>]+\)))*\)|[^\s`!()\[\]{};:'".,<>?])?");

            // Check if match was successful
            if (!match.Success)
            {
                return data;
            }

            // Extract data from match
            data.Scheme = match.Groups[1].Value;
            data.Host = match.Groups[2].Value;
            data.Path = match.Groups[3].Value;
            data.QueryString = match.Groups[4].Value;

            return data;
        }
    }
}

