```csharp
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;

public static string ExtractContent(string url)
{
    // Create a new HTTP client
    using var client = new HttpClient();

    // Set the HTTP method and URL
    var request = new HttpRequestMessage(HttpMethod.Get, url);

    // Add the Accept header with the desired content type
    request.Headers.Accept.Add(new MediaTypeHeaderValue("text/html"));

    // Send the request and get the response
    var response = client.SendAsync(request).Result;

    // Check the status code
    if (response.StatusCode != HttpStatusCode.OK)
    {
        throw new Exception($"Failed to extract content from {url}: {response.StatusCode}");
    }

    // Read the response content
    var content = response.Content;

    // Check if the content is HTML
    if (content.Headers.ContentType.MediaType != "text/html")
    {
        throw new Exception($"Content at {url} is not HTML");
    }

    // Read the HTML content
    var html = content.ReadAsStringAsync().Result;

    // Extract the content
    var doc = new HtmlDocument();
    doc.LoadHtml(html);

    // Get the content element
    var contentElement = doc.DocumentNode.SelectSingleNode("//div[@class='content']");

    // Extract the text content
    var text = contentElement.InnerText;

    // Return the extracted text
    return text;
}
