using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using System;
using System.Net.Http;
using System.Threading.Tasks;

namespace UrlFetcher.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlFetcherController : ControllerBase
    {
        private readonly ILogger<UrlFetcherController> _logger;

        public UrlFetcherController(ILogger<UrlFetcherController> logger)
        {
            _logger = logger;
        }

        [HttpGet("fetch")]
        public async Task<IActionResult> FetchUrlAsync(string url)
        {
            if (string.IsNullOrEmpty(url))
            {
                return BadRequest("URL is required.");
            }

            try
            {
                var httpClient = new HttpClient();
                var response = await httpClient.GetAsync(url);

                if (response.StatusCode == HttpStatusCode.OK)
                {
                    var content = await response.Content.ReadAsStringAsync();
                    return Ok(content);
                }
                else
                {
                    return StatusCode(response.StatusCode);
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error fetching URL.");
                return StatusCode(500);
            }
        }
    }
}
