// Controllers/UrlController.cs

using System;
using System.Net;
using System.Net.Http;
using System.Text.RegularExpressions;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;

namespace MyWebApi.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlController : ControllerBase
    {
        [HttpPost]
        public async Task<IActionResult> ExtractDataFromUrl(string url)
        {
            if (string.IsNullOrWhiteSpace(url))
            {
                return BadRequest("Invalid URL");
            }

            var httpClient = new HttpClient();
            var response = await httpClient.GetAsync(url);

            if (!response.IsSuccessStatusCode)
            {
                return BadRequest("Unable to fetch data from URL");
            }

            var content = await response.Content.ReadAsStringAsync();
            var data = ExtractDataFromHtml(content);

            return Ok(data);
        }

        private static string ExtractDataFromHtml(string html)
        {
            var pattern = @"<script>var data = \{.*?};</script>";
            var matches = Regex.Matches(html, pattern);

            if (matches.Count == 0)
            {
                return null;
            }

            var match = matches[0];
            var data = match.Value.Split("var data = ")[1].Split(";")[0];
            return data;
        }
    }
}

