// Controllers/UrlController.cs
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Mvc.Rendering;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using MyApp.Models;

namespace MyApp.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlController : ControllerBase
    {
        private readonly ILogger<UrlController> _logger;
        private readonly MyAppContext _context;

        public UrlController(ILogger<UrlController> logger, MyAppContext context)
        {
            _logger = logger;
            _context = context;
        }

        [HttpPost]
        public async Task<IActionResult> Post([FromBody] string url)
        {
            if (string.IsNullOrWhiteSpace(url))
            {
                return BadRequest("Invalid URL");
            }

            var urlData = await GetUrlData(url);

            if (urlData == null)
            {
                return BadRequest("Invalid URL");
            }

            _context.Urls.Add(urlData);
            await _context.SaveChangesAsync();

            return Ok(urlData);
        }

        private async Task<UrlData> GetUrlData(string url)
        {
            var urlData = _context.Urls.FirstOrDefault(u => u.Url == url);

            if (urlData != null)
            {
                return urlData;
            }

            var httpClient = new HttpClient();
            var response = await httpClient.GetAsync(url);

            if (response.StatusCode == HttpStatusCode.OK)
            {
                var content = await response.Content.ReadAsStringAsync();
                var title = GetTitle(content);
                var description = GetDescription(content);

                urlData = new UrlData
                {
                    Url = url,
                    Title = title,
                    Description = description
                };
            }

            return urlData;
        }

        private string GetTitle(string content)
        {
            var title = content.Split(new[] { "<title>" }, StringSplitOptions.None)[1];
            title = title.Split(new[] { "</title>" }, StringSplitOptions.None)[0];
            return title;
        }

        private string GetDescription(string content)
        {
            var description = content.Split(new[] { "<meta name=\"description\" content=\"" }, StringSplitOptions.None)[1];
            description = description.Split(new[] { "\"" }, StringSplitOptions.None)[0];
            return description;
        }
    }
}

