// Controllers/DownloadController.cs

using System;
using System.IO;
using System.Net;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;

namespace MyApp.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class DownloadController : ControllerBase
    {
        [HttpGet("{url}")]
        public async Task<IActionResult> Download(string url)
        {
            if (string.IsNullOrEmpty(url))
            {
                return BadRequest("Url is required");
            }

            try
            {
                var request = WebRequest.Create(url);
                var response = await request.GetResponseAsync();
                var stream = response.GetResponseStream();
                var buffer = new byte[1024 * 1024];
                var length = (int)response.ContentLength;
                var total = 0;
                var output = new MemoryStream();

                while (true)
                {
                    var read = stream.Read(buffer, 0, buffer.Length);
                    if (read == 0)
                    {
                        break;
                    }

                    output.Write(buffer, 0, read);
                    total += read;
                }

                return File(output.ToArray(), response.ContentType, response.ResponseUri.Segments.Last());
            }
            catch (Exception e)
            {
                return StatusCode(500, $"Failed to download file: {e.Message}");
            }
        }
    }
}
