using System.Net;
using System.IO;
using Microsoft.AspNetCore.Mvc;

namespace MyWebAPI.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class DownloadController : ControllerBase
    {
        [HttpGet("{url}")]
        public async Task<IActionResult> Download(string url)
        {
            // Validate the URL
            if (!Uri.TryCreate(url, UriKind.Absolute, out var uri))
            {
                return BadRequest("Invalid URL");
            }

            // Create a request for the URL
            var request = WebRequest.CreateHttp(uri);

            // Set the request headers
            request.Method = WebRequestMethods.Http.Get;
            request.UserAgent = "MyWebAPI";
            request.Accept = "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.9";

            // Get the response
            var response = await request.GetResponseAsync();

            // Check if the response is successful
            if (response.StatusCode != HttpStatusCode.OK)
            {
                return BadRequest("Failed to download content");
            }

            // Get the response stream
            var stream = response.GetResponseStream();

            // Create a memory stream to write the content to
            var memoryStream = new MemoryStream();

            // Read the response stream and write it to the memory stream
            var buffer = new byte[1024];
            int read;
            while ((read = await stream.ReadAsync(buffer, 0, buffer.Length)) > 0)
            {
                memoryStream.Write(buffer, 0, read);
            }

            // Return the downloaded content as a file
            return File(memoryStream.ToArray(), "application/octet-stream", Path.GetFileName(uri.AbsolutePath));
        }
    }
}

