private static Dictionary<string, string> ExtractDataFromHtml(string html)
{
    // Use a HTML parser to extract the data from the HTML content
    var parser = new HtmlParser();
    var document = parser.Parse(html);

    // Extract the data from the HTML document
    var data = new Dictionary<string, string>();
    foreach (var element in document.Body.SelectNodes("//div[@class='data']"))
    {
        var key = element.GetAttribute("data-key");
        var value = element.GetAttribute("data-value");
        data.Add(key, value);
    }

    return data;
}
