using System.Net;
using System.IO;

public static void DownloadFile(string url)
{
    // Create a new HttpWebRequest object
    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);

    // Set the User-Agent header to a valid browser string
    request.UserAgent = "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/83.0.4103.116 Safari/537.36";

    // Set the Accept header to "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.9"
    request.Accept = "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.9";

    // Set the Referer header to the URL of the current page
    request.Referer = "https://www.example.com/";

    // Set the Cookie header to a valid cookie string
    request.Cookie = "cookie=value";

    // Set the timeout for the request to 30 seconds
    request.Timeout = 30000;

    // Get the response from the server
    HttpWebResponse response = (HttpWebResponse)request.GetResponse();

    // Get the response stream
    Stream responseStream = response.GetResponseStream();

    // Create a new file to save the downloaded data
    FileStream fileStream = new FileStream("downloaded_file.txt", FileMode.Create);

    // Read the response stream and write it to the file stream
    byte[] buffer = new byte[1024];
    int bytesRead;
    while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0)
    {
        fileStream.Write(buffer, 0, bytesRead);
    }

    // Close the file stream
    fileStream.Close();

    // Close the response stream
    responseStream.Close();

    // Close the response
    response.Close();
}
