using System;
using System.Text.RegularExpressions;

public static class UrlDataExtractor
{
    public static UrlData ExtractData(string url)
    {
        // Validate input
        if (string.IsNullOrEmpty(url))
        {
            throw new ArgumentException("Invalid URL", nameof(url));
        }

        // Extract protocol and host
        string protocol = Regex.Match(url, @"^(?<protocol>https?):", RegexOptions.IgnoreCase).Groups["protocol"].Value;
        string host = Regex.Match(url, @"^(?<host>[^/]+)", RegexOptions.IgnoreCase).Groups["host"].Value;

        // Extract path and query string
        string path = Regex.Match(url, @"^(?<path>/[^?]+)", RegexOptions.IgnoreCase).Groups["path"].Value;
        string queryString = Regex.Match(url, @"^(?<queryString>\?.*)", RegexOptions.IgnoreCase).Groups["queryString"].Value;

        // Extract parameters
        string[] parameters = queryString.Split('&');
        Dictionary<string, string> parameterDictionary = new Dictionary<string, string>();
        foreach (string parameter in parameters)
        {
            string[] keyValue = parameter.Split('=');
            if (keyValue.Length == 2)
            {
                parameterDictionary.Add(keyValue[0], keyValue[1]);
            }
        }

        // Return extracted data
        return new UrlData(protocol, host, path, queryString, parameterDictionary);
    }
}

using System;
using System.Collections.Generic;

public class UrlData
{
    public string Protocol { get; }
    public string Host { get; }
    public string Path { get; }
    public string QueryString { get; }
    public Dictionary<string, string> Parameters { get; }

    public UrlData(string protocol, string host, string path, string queryString, Dictionary<string, string> parameters)
    {
        Protocol = protocol;
        Host = host;
        Path = path;
        QueryString = queryString;
        Parameters = parameters;
    }
}

