using System;
using System.IO;
using System.Net;
using System.Text.RegularExpressions;

public class UrlDataExtractor
{
    public static string ExtractData(string url)
    {
        if (string.IsNullOrEmpty(url))
        {
            throw new ArgumentException("URL cannot be null or empty", nameof(url));
        }

        var webClient = new WebClient();
        var response = webClient.DownloadString(url);

        var data = new Regex("(?<key>\\w+)=(?<value>\\w+)").Matches(response);

        var extractedData = new Dictionary<string, string>();

        foreach (Match match in data)
        {
            extractedData.Add(match.Groups["key"].Value, match.Groups["value"].Value);
        }

        return extractedData;
    }
}
