// File: UrlDataExtractor.cs

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;

namespace UrlDataExtractor
{
    public class UrlDataExtractor
    {
        public static Dictionary<string, string> ExtractData(string url)
        {
            var data = new Dictionary<string, string>();

            // Extract the protocol
            var protocol = Regex.Match(url, @"^(?<protocol>[^:]+):");
            if (protocol.Success)
            {
                data.Add("Protocol", protocol.Groups["protocol"].Value);
            }

            // Extract the host
            var host = Regex.Match(url, @"^(?<protocol>[^:]+):\/\/(?<host>[^/]+)");
            if (host.Success)
            {
                data.Add("Host", host.Groups["host"].Value);
            }

            // Extract the path
            var path = Regex.Match(url, @"^(?<protocol>[^:]+):\/\/(?<host>[^/]+)(?<path>/[^?]+)");
            if (path.Success)
            {
                data.Add("Path", path.Groups["path"].Value);
            }

            // Extract the query string
            var queryString = Regex.Match(url, @"^(?<protocol>[^:]+):\/\/(?<host>[^/]+)(?<path>/[^?]+)\?(?<queryString>[^#]+)");
            if (queryString.Success)
            {
                data.Add("QueryString", queryString.Groups["queryString"].Value);
            }

            // Extract the fragment
            var fragment = Regex.Match(url, @"^(?<protocol>[^:]+):\/\/(?<host>[^/]+)(?<path>/[^?]+)\?(?<queryString>[^#]+)#(?<fragment>[^#]+)");
            if (fragment.Success)
            {
                data.Add("Fragment", fragment.Groups["fragment"].Value);
            }

            return data;
        }
    }
}

