using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using System.Text.RegularExpressions;

namespace MyApp.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlController : ControllerBase
    {
        [HttpPost("extract")]
        public ActionResult<UrlData> ExtractUrlData(string url)
        {
            // Validate the URL
            if (!Regex.IsMatch(url, "^https?://", RegexOptions.IgnoreCase))
            {
                return BadRequest("Invalid URL");
            }

            // Extract the data from the URL
            var data = new UrlData
            {
                Protocol = url.Substring(0, url.IndexOf("://")),
                Host = url.Substring(url.IndexOf("://") + 3, url.IndexOf("/", 3) - 3),
                Path = url.Substring(url.IndexOf("/", 3)),
                Query = new Dictionary<string, string>(),
                Fragment = string.Empty
            };

            // Parse the query string
            var queryString = url.Substring(url.IndexOf("?") + 1);
            var queryParams = queryString.Split('&');
            foreach (var queryParam in queryParams)
            {
                var param = queryParam.Split('=');
                data.Query.Add(param[0], param[1]);
            }

            // Parse the fragment
            var fragment = url.Substring(url.IndexOf("#") + 1);
            data.Fragment = fragment;

            return Ok(data);
        }
    }

    // Models/UrlData.cs
