using System.Net.Http;

public static async Task<string> GetDataFromUrlAsync(string url)
{
    // Create a new HttpClient object
    using var client = new HttpClient();

    // Set the HTTP method to GET
    client.DefaultRequestHeaders.Method = HttpMethod.Get;

    // Set the URL to the user-specified URL
    client.BaseAddress = new Uri(url);

    // Send the request and get the response
    var response = await client.SendAsync(request);

    // Check if the response was successful
    if (response.IsSuccessStatusCode)
    {
        // Get the response data as a string
        var data = await response.Content.ReadAsStringAsync();

        // Return the data
        return data;
    }
    else
    {
        // Throw an exception if the response was not successful
        throw new Exception($"Error fetching data from URL {url}: {response.StatusCode} {response.ReasonPhrase}");
    }
}
