// Controllers/DataController.cs
using Microsoft.AspNetCore.Mvc;
using System.Net.Http;
using System.IO;

namespace MyApp.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class DataController : ControllerBase
    {
        [HttpGet("{id}")]
        public IActionResult GetData(string id)
        {
            // Check if the ID is valid
            if (!IsValidId(id))
            {
                return BadRequest();
            }

            // Download the data from the URL
            var url = $"https://example.com/data/{id}";
            var client = new HttpClient();
            var response = await client.GetAsync(url);
            var data = await response.Content.ReadAsByteArrayAsync();

            // Return the data as a file
            return File(data, "application/octet-stream", $"data-{id}.bin");
        }

        private bool IsValidId(string id)
        {
            // Check if the ID is a valid GUID
            if (Guid.TryParse(id, out var guid))
            {
                return true;
            }

            // Check if the ID is a valid URL
            if (Uri.TryCreate(id, UriKind.Absolute, out var uri) && uri.Scheme == "http")
            {
                return true;
            }

            return false;
        }
    }
}
