using Microsoft.AspNetCore.Mvc;

namespace MyApp.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class UrlController : ControllerBase
    {
        [HttpPost]
        public ActionResult<UrlData> ExtractUrlData(string url)
        {
            // Validate the input
            if (string.IsNullOrWhiteSpace(url))
            {
                return BadRequest("Invalid URL");
            }

            // Extract the data from the URL
            var data = new UrlData();
            data.Scheme = new Uri(url).Scheme;
            data.Host = new Uri(url).Host;
            data.Port = new Uri(url).Port;
            data.Path = new Uri(url).AbsolutePath;
            data.Query = new Uri(url).Query;
            data.Fragment = new Uri(url).Fragment;

            return Ok(data);
        }
    }
}

