// Controllers/DataExtractorController.cs
using System.Net;
using System.Text;
using System.Text.RegularExpressions;

namespace DataExtractor
{
    [ApiController]
    [Route("[controller]")]
    public class DataExtractorController : ControllerBase
    {
        [HttpGet("{url}")]
        public async Task<ActionResult<string>> ExtractData(string url)
        {
            if (string.IsNullOrEmpty(url))
            {
                return BadRequest("Invalid URL");
            }

            // Create a new HttpWebRequest object
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);

            // Set the request method to GET
            request.Method = "GET";

            // Set the request headers
            request.Headers.Add("Accept", "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8");
            request.Headers.Add("User-Agent", "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/80.0.3987.149 Safari/537.36");

            // Get the response from the server
            HttpWebResponse response = (HttpWebResponse)await request.GetResponseAsync();

            // Check if the response is successful
            if (response.StatusCode == HttpStatusCode.OK)
            {
                // Get the response stream
                Stream responseStream = response.GetResponseStream();

                // Create a new StreamReader object
                StreamReader reader = new StreamReader(responseStream);

                // Read the response stream
                string responseText = await reader.ReadToEndAsync();

                // Close the response stream
                responseStream.Close();

                // Return the extracted data
                return Ok(responseText);
            }
            else
            {
                // Return an error message
                return BadRequest($"Invalid URL: {url}");
            }
        }
    }
}
